Math.lerp = (a, b, n) =&gt; (1 - n) * a + n * b;

const getStyle = (el, attr) **=&gt;** {
    try {
        return window.getComputedStyle
            ? window.getComputedStyle(el)[attr]
            : el.currentStyle[attr];
    } catch (e) {}
    return "";
};

class Cursor {
    constructor() {
        this.pos = {curr: null, prev: null};
        this.pt = [];
        this.create();
        this.init();
        this.render();
    }

    move(left, top) {
        this.cursor.style["left"] = `${left}px`;
        this.cursor.style["top"] = `${top}px`;
    }

    create() {
        if (!this.cursor) {
            this.cursor = document.createElement("div");
            this.cursor.id = "cursor";
            this.cursor.classList.add("hidden");
            document.body.append(this.cursor);
        }

        var el = document.getElementsByTagName('*');
        for (**let** i = 0; i &lt; el.length; i++)
            if (getStyle(el[i], "cursor") == "pointer")
                this.pt.push(el[i].outerHTML);

        document.body.appendChild((this.scr = document.createElement("style")));
        // ​这里​改变​鼠标​指针​的​颜色​ ​由​svg​生成​
        this.scr.innerHTML = `* {cursor: url("data:image/svg+xml,&lt;svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 8 8' width='8px' height='8px'&gt;&lt;circle cx='4' cy='4' r='4' opacity='.5'/&gt;&lt;/svg&gt;") 4 4, auto}`;
    }

    refresh() {
        this.scr.remove();
        this.cursor.classList.remove("hover");
        this.cursor.classList.remove("active");
        this.pos = {curr: null, prev: null};
        this.pt = [];

        this.create();
        this.init();
        this.render();
    }

    init() {
        document.onmouseover  = e =&gt; this.pt.includes(e.target.outerHTML) &amp;&amp; this.cursor.classList.add("hover");
        document.onmouseout   = e =&gt; this.pt.includes(e.target.outerHTML) &amp;&amp; this.cursor.classList.remove("hover");
        document.onmousemove  = e =&gt; {(this.pos.curr == null) &amp;&amp; this.move(e.clientX - 8, e.clientY - 8); this.pos.curr = {x: e.clientX - 8, y: e.clientY - 8}; this.cursor.classList.remove("hidden");};
        document.onmouseenter = e =&gt; this.cursor.classList.remove("hidden");
        document.onmouseleave = e =&gt; this.cursor.classList.add("hidden");
        document.onmousedown  = e =&gt; this.cursor.classList.add("active");
        document.onmouseup    = e =&gt; this.cursor.classList.remove("active");
    }

    render() {
        if (this.pos.prev) {
            this.pos.prev.x = Math.lerp(this.pos.prev.x, this.pos.curr.x, 0.15);
            this.pos.prev.y = Math.lerp(this.pos.prev.y, this.pos.curr.y, 0.15);
            this.move(this.pos.prev.x, this.pos.prev.y);
        } else {
            this.pos.prev = this.pos.curr;
        }
        requestAnimationFrame(() =&gt; this.render());
    }
}

(() =&gt; {
    CURSOR = new Cursor();
    // ​需要​重新​获取​列表​时​，​使用​ CURSOR.refresh()
})();

// ​生成​随机​颜色​
function randomColor() {
    const colors = ['#FF1461', '#18FF92', '#5A87FF', '#FBF38C'];
    return colors[Math.floor(Math.random() * colors.length)];
}

// ​生成​烟花​效果​
function createFirework(x, y) {
    const fireworkContainer = document.createElement('div');
    fireworkContainer.classList.add('fireworks');
    fireworkContainer.style.left = `${x}px`;
    fireworkContainer.style.top = `${y}px`;
    document.body.appendChild(fireworkContainer);

    for (let i = 0; i &lt; 25; i++) {
        const firework = document.createElement('div');
        firework.classList.add('firework');
        firework.style.backgroundColor = randomColor();
        firework.style.width = '5px';
        firework.style.height = '5px';
        firework.style.left = `${Math.random() * 80 - 50}px`;
        firework.style.top = `${Math.random() * 80 - 50}px`;
        firework.style.animation = `firework 1s ease-out forwards, fall 1s ${Math.random() * 0.5}s forwards`;
        fireworkContainer.appendChild(firework);
    }

    setTimeout(() =&gt; {
        fireworkContainer.remove();
    }, 300);
}
// ​监听​单击​事件​
document.addEventListener('click', (event) =&gt; {
    const x = event.clientX;
    const y = event.clientY + window.scrollY;
    createFirework(x, y);
});
